Scripts.OnEvent:
if (self:IsVisible()) then
	PanelTemplates_TabResize(self, 0, nil, 36, self:GetParent().maxTabWidth or 88);
end

Scripts.OnEnter:
local buttonText = _G[self:GetName().."Text"];
if (buttonText and buttonText:IsTruncated()) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(buttonText:GetText());
end

Scripts.OnLoad:
self.Icon:SetTexture(MCFPAPERDOLL_SIDEBARS[self:GetID()].icon);
local tcoords = MCFPAPERDOLL_SIDEBARS[self:GetID()].texCoords;
self.Icon:SetTexCoord(tcoords[1], tcoords[2], tcoords[3], tcoords[4]);

Scripts.OnEnter:
if (MCFMOVING_STAT_CATEGORY == nil) then
	self.SortDownArrow:Show();
	self.SortUpArrow:Show();
end
self.SortDownArrow:Show();
self.SortUpArrow:Show();

Scripts.OnLeave:
if ( not self:IsMouseOver() and MCFMOVING_STAT_CATEGORY ~= self:GetParent()) then
	self.SortDownArrow:Hide();
	self.SortUpArrow:Hide();
end

Scripts.OnClick:
if self:GetParent().collapsed then
	MCFPaperDollFrame_ExpandStatCategory(self:GetParent());
	local categoryInfo = MCFPAPERDOLL_STATCATEGORIES[self:GetParent().Category];
	if (categoryInfo and MCFCharacterStatsPane.collapsedCVarName) then

		MCF_SetSettings(MCFCharacterStatsPane.collapsedCVarName, false, categoryInfo.id);
	end
else
	MCFPaperDollFrame_CollapseStatCategory(self:GetParent());
	local categoryInfo = MCFPAPERDOLL_STATCATEGORIES[self:GetParent().Category];
	if (categoryInfo and MCFCharacterStatsPane.collapsedCVarName) then

		MCF_SetSettings(MCFCharacterStatsPane.collapsedCVarName, true, categoryInfo.id);
	end
end

Scripts.OnDragStop:
MCFPaperDollStatCategory_OnDragStop(self:GetParent());
if (self:IsMouseOver()) then
	self.SortDownArrow:Show();
	self.SortUpArrow:Show();
else
	self.SortDownArrow:Hide();
	self.SortUpArrow:Hide();
end

Scripts.OnLeave:
self.Arrow:SetAlpha(0.4);
if ( not self:GetParent():IsMouseOver() ) then
	self:Hide();
	self:GetParent().SortDownArrow:Hide();
end

self.Arrow:SetAlpha(0.4);
if ( not self:GetParent():IsMouseOver() ) then
	self:Hide();
	self:GetParent().SortUpArrow:Hide();
end

Scripts.OnClick:
if (MCFCharacterFrame.Expanded) then
	MCF_SETTINGS["characterFrameCollapsed"] = "1"; 
	PlaySound(SOUNDKIT.IG_CHARACTER_INFO_CLOSE);
	MCFCharacterFrame_Collapse();
else
	MCF_SETTINGS["characterFrameCollapsed"] = "0"; 
	PlaySound(SOUNDKIT.IG_CHARACTER_INFO_OPEN);
	MCFCharacterFrame_Expand();
end
if (GameTooltip:GetOwner() == self) then
	self:GetScript("OnEnter")(self);
end

Scripts.OnLoad:
self:RegisterEvent("UNIT_PORTRAIT_UPDATE");
self:RegisterEvent("PLAYER_ENTERING_WORLD");

local tcoords = MCFPAPERDOLL_SIDEBARS[self:GetID()].texCoords;
self.Icon:SetTexCoord(tcoords[1], tcoords[2], tcoords[3], tcoords[4]);
self.Icon:SetSize(29, 31);
self.Icon:SetPoint("BOTTOM", 1, 0);

Scripts.OnEvent:
if ( event == "UNIT_PORTRAIT_UPDATE" ) then
	local unit = ...;
	if ( unit == "player" ) then
		SetPortraitTexture(self.Icon, unit);
	end
elseif ( event == "PLAYER_ENTERING_WORLD" ) then
	SetPortraitTexture(self.Icon, "player");
end
